---------------------------------------配置面板-------------------------------------TinyInspectDB = {    showInspectCompare  = false, --觀察目標時顯示玩家物品列表    hidePlayerPaperDoll = false, --隱藏玩家物品等級    hideBagItemLevel    = false, --隱藏背包物品等級        hideBagItemSlotString = false, --隱藏背包物品部位文字        showColoredItemLevelString = false, --裝等文字根據品質顯示顔色    PartyItemLevel  = true, --報告隊友裝等        PartyItemLevelToParty = true,        PartyItemLevelToSelf = false,    showMouseItemLevel  = false, --顯示鼠標提示裝等    showStatePercent    = false, --屬性顯示百分比數值    showRectangularFrame = false, --邊框直角化    showItemRectangularBorder = true, --物品邊框直角化    showColoredLabel = true, --觀察面板高亮橙裝武器標簽}local TinyInspectOptions = {    {        key  = "showStatePercent",        zhTW = "顯示屬性百分比數值",        zhCN = "显示属性百分比数值",        enUS = "Show Percentage Stats",    },    {        key  = "hidePlayerPaperDoll",        zhTW = "隱藏玩家面板物品等級",        zhCN = "隐藏玩家面板物品等级",        enUS = "Hide PaperDoll ItemLevel",    },    {        key  = "hideBagItemLevel",        zhTW = "隱藏物品等級文字",        zhCN = "隐藏物品等级文字",        enUS = "Hide Bag&Bank ItemLevel",        child = {            {                key  = "hideBagItemSlotString",                zhTW = "隱藏背包物品部位文字",                zhCN = "隐藏背包物品部位文字",                enUS = "Hide Bag&Bank ItemSlotString",            },            {                key  = "showColoredItemLevelString",                zhTW = "文字色彩跟隨物品品質",                zhCN = "文字色彩跟随物品品质",                enUS = "Show Colored ItemLevel String",            },        },    },    {        key  = "PartyItemLevel",        zhTW = "報告隊友裝等",        zhCN = "报告队友装等",        enUS = "Get PartyItemLevel",        child = {            {                key  = "PartyItemLevelToParty",                zhTW = "報告到隊伍頻道",                zhCN = "报告到队伍频道",                enUS = "Send PartyItemLevel To [Party]",            },            {                key  = "PartyItemLevelToSelf",                zhTW = "僅自己面板報告",                zhCN = "仅自己面板报告",                enUS = "Send PartyItemLevel To Console",            },        },    },    {        key  = "showMouseItemLevel",        zhTW = "顯示鼠標目標裝備等級",        zhCN = "显示鼠标目标装备等级",        enUS = "Show MouseoverUnit ItemLevel",    },    {        key  = "showInspectCompare",        zhTW = "觀察同時顯示自己物品列表框",        zhCN = "观察同时显示自己物品列表框",        enUS = "Show Own ItemList When Inspecting",    },    {        key  = "showColoredLabel",        zhTW = "高亮顯示橙裝和武器",        zhCN = "高亮显示橙装和武器",        enUS = "Show Colored Label",    },    {        key  = "showItemRectangularBorder",        zhTW = "物品邊框直角化顯示",        zhCN = "物品边框直角化显示",        enUS = "Show Rectangular ItemBorder",    },    {        key  = "showRectangularFrame",        zhTW = "觀察面板直角化顯示 |cff00eeee(需重載)|r",        zhCN = "观察面板直角化显示 |cff00eeee(需重载)|r",        enUS = "Show Rectangular Frame |cff00eeee(needs reload)|r",    },}local addon = ...local frame = CreateFrame("Frame", nil, UIParent)frame.title = frame:CreateFontString(nil, "OVERLAY", "GameFontNormalLarge")frame.title:SetPoint("TOPLEFT", 16, -16)frame.title:SetText(addon)frame.name = addonframe:RegisterEvent("VARIABLES_LOADED")frame:SetScript("OnEvent", function(self, event, ...)    local checkbox    for i, v in ipairs(TinyInspectOptions) do        checkbox = frame["checkbox"..i]        checkbox:SetChecked(TinyInspectDB[checkbox.key])        if (v.child) then            for ii, vv in ipairs(v.child) do                checkbox = frame["checkbox"..i.."child"..ii]                checkbox:SetChecked(TinyInspectDB[checkbox.key])            end        end    endend)do    local locale = GetLocale()    local function CheckboxOnClick(self)        TinyInspectDB[self.key] = self:GetChecked()    end    local checkbox, lastbox    local xpos = 0    for i, v in ipairs(TinyInspectOptions) do        checkbox = CreateFrame("CheckButton", nil, frame, "InterfaceOptionsCheckButtonTemplate")        checkbox.key = v.key        checkbox.Text:SetText(v[locale] or v.key)        checkbox:SetScript("OnClick", CheckboxOnClick)        if (i == 1) then            checkbox:SetPoint("TOPLEFT", frame.title, "BOTTOMLEFT", 12, -10)        else            checkbox:SetPoint("TOPLEFT", lastbox, "BOTTOMLEFT", xpos, -6)        end        frame["checkbox"..i] = checkbox        lastbox = checkbox        xpos = 0        if (v.child) then            for ii, vv in ipairs(v.child) do                checkbox = CreateFrame("CheckButton", nil, frame, "InterfaceOptionsCheckButtonTemplate")                checkbox.key = vv.key                checkbox.Text:SetText(vv[locale] or vv.key)                checkbox:SetScript("OnClick", CheckboxOnClick)                checkbox:SetPoint("TOPLEFT", lastbox, "BOTTOMLEFT", ii==1 and 30 or 0, -4)                checkbox:SetScale(0.9)                frame["checkbox"..i.."child"..ii] = checkbox                lastbox = checkbox            end            xpos = -30        end    endendInterfaceOptions_AddCategory(frame)SLASH_TinyInspect1 = "/tinyinspect"function SlashCmdList.TinyInspect(msg, editbox)    InterfaceOptionsFrame_OpenToCategory(frame)    InterfaceOptionsFrame_OpenToCategory(frame)end