--------------------------------------- 鼠标滑过显示装等和天赋 Author: M-------------------------------------local LibSchedule = LibStub:GetLibrary("LibSchedule.7000")local LibItemInfo = LibStub:GetLibrary("LibItemInfo.7000")--更新周期local updateElapsed = 600--緩存(全局變量)TinyInspectCache = TinyInspectCache or {}TinyInspectCache.InspectDB = {}--最近一次可觀察的GUIDlocal currentGUID--讀取或存儲local function InspectDBFactory(guid, itemLevel, specName, hp)    if (not TinyInspectCache.InspectDB[guid]) then        TinyInspectCache.InspectDB[guid] = { lvl = itemLevel, spec = specName, t = time() + updateElapsed }    elseif (itemLevel and itemLevel > 0) then        TinyInspectCache.InspectDB[guid].lvl  = itemLevel        TinyInspectCache.InspectDB[guid].spec = specName        TinyInspectCache.InspectDB[guid].t    = time() + updateElapsed    end    if (hp) then        TinyInspectCache.InspectDB[guid].hp = hp    end    if (TinyInspectCache.InspectDB[guid].t < time()) then        return false    end    return TinyInspectCache.InspectDB[guid].lvl, TinyInspectCache.InspectDB[guid].specend--檢查數據(3秒内同guid防止多次發送)(天賦改變HP一般也變)local function needsUpdate(guid, hp)    if (not TinyInspectCache.InspectDB[guid]) then        TinyInspectCache.InspectDB[guid] = { t = time() + 3 }        return true    end    if (TinyInspectCache.InspectDB[guid].t < time() or TinyInspectCache.InspectDB[guid].lvl == 0) then        return true    end    if (hp and TinyInspectCache.InspectDB[guid].hp ~= hp) then        return true    endend--获取天赋专精local function GetUnitSpec(unit)    if (UnitLevel(unit) > 10) then        local specID, specName = GetInspectSpecialization(unit)        if (specID and specID > 0) then            specName = select(2, GetSpecializationInfoByID(specID))        end        return specName    endend--顯示到Tooltiplocal LevelLabel = STAT_AVERAGE_ITEM_LEVEL .. ": "local SpecLabel  = TALENT .. SPECIALIZATION .. ": "local function appendToGameTooltip(guid, itemLevel, specName)    local _, unit = GameTooltip:GetUnit()    if (not unit or UnitGUID(unit) ~= guid) then return end        local line, text, levelLine, specLine    for i = 2, GameTooltip:NumLines() do        line = _G["GameTooltipTextLeft" .. i]        text = line:GetText()        if (text and strfind(text, LevelLabel)) then            levelLine = line        elseif (text and strfind(text, SpecLabel)) then            specLine = line        end    end    --物品等级    if (itemLevel) then        text = "|cffffeeaa" .. LevelLabel .. "|cffffffff" .. itemLevel        if (levelLine) then            levelLine:SetText(text)        else            GameTooltip:AddLine(text)        end    end    --天赋专精    if (specName) then        text = "|cffffeeaa" .. SpecLabel .. "|cffffffff" .. specName        if (specLine) then            specLine:SetText(text)        else            GameTooltip:AddLine(text)        end    end        GameTooltip:Show()end--當tootip有unit的時候執行觀察GameTooltip:HookScript("OnTooltipSetUnit", function(self)    if (not TinyInspectDB or not TinyInspectDB.showMouseItemLevel) then        return    end    local _, unit = self:GetUnit()    local thisGUID = UnitGUID(unit)    if (InspectFrame and InspectFrame.unit) then return end    if (not thisGUID) then return end    if (needsUpdate(thisGUID, UnitHealthMax(unit))) then        if (not CanInspect(unit)) then return end        if (thisGUID ~= currentGUID) then            ClearInspectPlayer()        end        currentGUID = thisGUID        return NotifyInspect(unit)    end    local itemLevel, specName = InspectDBFactory(thisGUID)    if (itemLevel) then        appendToGameTooltip(thisGUID, floor(itemLevel), specName)    endend)--事件處理local frame = CreateFrame("Frame", nil, UIParent)frame:RegisterEvent("INSPECT_READY")frame:SetScript("OnEvent", function(self, event, arg1, ...)    if (not TinyInspectDB or not TinyInspectDB.showMouseItemLevel) then        return    end    if (event == "INSPECT_READY" and arg1 == UnitGUID("mouseover")) then        LibSchedule:AddTask({            identity  = arg1,            timer     = 0.1,            elasped   = 0.5,            expired   = GetTime() + 3,            onStart   = function(self) appendToGameTooltip(self.identity, "...") end,            onTimeout = function(self) appendToGameTooltip(self.identity, "Failed") end,            onExecute = function(self)                if (UnitGUID("mouseover") ~= self.identity) then return end                local unknownCount, itemLevel = LibItemInfo:GetUnitItemLevel("mouseover")                if (unknownCount == 0) then                    local specName = GetUnitSpec("mouseover")                    local hp = UnitHealthMax("mouseover")                    InspectDBFactory(self.identity, itemLevel, specName, hp)                    appendToGameTooltip(self.identity, floor(itemLevel), specName)                    return true                end            end,        })    endend)