				["init"] = {
					["custom"] = "-- EDIT THIS IF YOU NEED\n-- text output\naura_env.text_out = false         -- paste false if no text on bar\n-- ping\naura_env.check_ping = true       -- paste true, if you want to correct your bar with ping, paste false if dont\naura_env.myApprPing = 0.2        -- your appreciate ping\n\n-- energy type\naura_env.my_energy_type = 8\n\n-- all noninstant spells, changing energy\naura_env.SpellList = {\n    {\n        id = 194153,\n        eng = 12\n    },\n    {\n        id = 190984,\n        eng = 8\n    },\n    {\n        id = 274281,\n        eng = 10,\n        nomod = true\n    },\n    {\n        id = 274282,\n        eng = 20,\n        nomod = true\n    },\n    {\n        id = 274283,\n        eng = 40,\n        nomod = true\n    },\n    {\n        id = 202347,\n        eng = 8,\n        nomod = true\n    },\n}\n\n-- energy modificators\n-- if talent paste type = 'talent', id = talent string talent nomber\n-- if aura paste type = 'aura', id = aura id\n-- mod = energy multiplier, as an example s2m gives +100% ins for SP, so multiplier = 1(base ins)+1(add ins) => paste mod = 2\naura_env.Modificators  = {}\n\n----------------------------------------------------\n-- DONT EDIT NEXT\n\n-- initialisations\naura_env.saveEpositionOnPing = 0\naura_env.saveTpositionOnPing = 0\naura_env.Position = 0\naura_env.MaxPosition = 1\n\n-- function to check is there any active multipliers\naura_env.Mod_check = function()\n    local sum_mult = 1\n    for k, v in pairs(aura_env.Modificators) do\n        if v.type == 'talent' \n        then\n            if select(4, GetTalentInfo(math.floor(v.id/10)*10,v.id%10,GetActiveSpecGroup()))\n            then \n                sum_mult = sum_mult*v.mod\n            end\n        elseif v.type == 'aura'\n        then\n            for i =1, 40 do\n                if select(10, UnitBuff(\"player\",i)) == v.id\n                then\n                    sum_mult = sum_mult*v.mod\n                end\n            end\n        end\n    end\n    \n    return sum_mult\nend\n\n--function to find new position of energy\naura_env.E_Position = function(add_eng, stTime, edTime)\n    local adding_cast_eng = 0\n    local NewPosition = UnitPower('player',aura_env.my_energy_type)+add_eng;\n    local CurPosition = UnitPower('player',aura_env.my_energy_type);\n    local MaxPosition = UnitPowerMax(\"player\", aura_env.my_energy_type);\n    \n    if stTime\n    then\n        adding_cast_eng = add_eng*(GetTime()*1000 -stTime)/(edTime-stTime)\n    end\n    \n    if aura_env.check_ping and stTime\n    then\n        aura_env.saveEpositionOnPing = NewPosition\n        aura_env.saveTpositionOnPing = GetTime()\n    elseif aura_env.myApprPing+aura_env.saveTpositionOnPing > GetTime()\n    then\n        NewPosition = aura_env.saveEpositionOnPing\n    end\n    \n    \n    if NewPosition < CurPosition\n    then\n        return math.max(NewPosition, 0), CurPosition, MaxPosition, CurPosition+adding_cast_eng, math.max(NewPosition, 0)\n    else\n        return CurPosition, math.min(NewPosition, MaxPosition), MaxPosition, CurPosition+adding_cast_eng, math.min(NewPosition, MaxPosition)\n    end\nend",
